var fis = module.exports = require('fis3');
fis.require.prefixes.unshift('yuefe');
fis.cli.name = 'yuefe';
fis.cli.info = require('./package.json');


var mode = fis.project.currentMedia('prod') == 'prod' ? 'prod' : 'dev';
var mode_str = '调试状态';

switch(mode)
{
	case 'prod':
	mode_str = '发布状态'
	break;
	case 'predev':
	case 'dev':
	mode_str = '开发状态'
	break;
}

fis.log.info("yuefe @flzt版本 启动中...");
fis.log.warning("当前开发模式是 ["+mode_str+"]");



